WinRatePercents_1.query:
q = load "Opty_Acc_Split_Tag";
{{cell(OptyRoleToggle_1.selection, 0, "saql_filter").asString()}}"{{cell(UserData.selection, 0, "Id").asString()}}";
q = filter q by 'Opportunity.StageName' in ["Closed Lost", "Closed Won"];
q = group q by 'Opportunity.StageName';
q = foreach q generate q.'Opportunity.StageName' as 'Opportunity.StageName', sum(q.'gCRM_Split_Amount__c') as 'A', unique(q.'Opportunity.gCRM_Opportunity_ID__c') as 'B';
q = group q by 'Opportunity.StageName';
q = foreach q generate 'Opportunity.StageName', first(A) as 'A', first(B) as 'B', sum(B)/sum(sum(B)) over ([..] partition by all) as 'C', sum(A)/sum(sum(A)) over ([..] partition by all) as 'D';
q = order q by 'Opportunity.StageName' desc;
q = limit q 2000;

